#include <stdint.h>
#include <sgx_trts.h>
#include <stdio.h>
#include <stdlib.h>
#include <stdarg.h>

int Q = 93529;
int x_pk = 46261;
int PAGE_SIZE = 4096*5;

__attribute__((aligned(4096))) int a;

int add(int x, int y) {
    return x + y;
}

// Vulnerable function
int mod(int v, int modulus) {
    char seperator[PAGE_SIZE];
    if (v < modulus) {
        return v;
    } else {
        int remainder = divrem(v, modulus);
        return remainder; 
    }
}

void printf(const char *fmt, ...)
{
    char buf[BUFSIZ] = {'\0'};
    va_list ap;
    va_start(ap, fmt);
    vsnprintf(buf, BUFSIZ, fmt, ap);
    va_end(ap);
    uprint(buf);
}

void* get_a_addr( void )
{
    return &a;
}

void enclave_dummy_call(void)
{
    a++;
    return;
}

int ECDSA_sign(char* msg) {
    char seperator[PAGE_SIZE];

    int hashed_msg = hash(msg) % Q;
    int modded_msg = mod(hashed_msg, Q);
    int k = random_int(1, Q);
    int k_inverse = modular_inv(k, Q);
    int r = F(k, Q);
    int rx = mul(r, x_pk);
    rx = mod(rx, Q);
    // start of side channel
    int sum = add(modded_msg, rx);
    sum = mod(sum, Q);
    int s = mul(k_inverse, sum);
    s = mod(s, Q);
    printf("Signed value %d\n",  s);
    return r;
}

// djb2 from http://www.cse.yorku.ca/~oz/hash.html
int hash(unsigned char *str) {
    char seperator[PAGE_SIZE];

    int hash = 5381;
    int c;
    while (c = *str++)
        hash = ((hash << 5) + hash) + c; /* hash * 33 + c */
    return hash;
}

int random_int(int start, int end) {
    char seperator[PAGE_SIZE];
    uint32_t val;
    sgx_read_rand((unsigned char *) &val, 4);
    return val % end;
}

/* Modular inverse from https://www.geeksforgeeks.org/multiplicative-inverse-under-modulo-m/ */
int modular_inv(int value, int modulus) {
    char seperator[PAGE_SIZE];

    int x, y;
    int g = gcdExtended(a, modulus, &x, &y);
    if (g != 1) {
        return -1;
    } else { 
        return (x%modulus + modulus) % modulus; 
    }
}
int gcdExtended(int a, int b, int *x, int *y) { 
    char seperator[PAGE_SIZE];

    if (a == 0) { 
        *x = 0, *y = 1; 
        return b; 
    } 
    int x1, y1; // To store results of recursive call 
    int gcd = gcdExtended(b%a, a, &x1, &y1); 
    *x = y1 - (b/a) * x1; 
    *y = x1; 
    return gcd; 
}

int F(int v, int Q) {
    char seperator[4096*2] = "  ";
    printf(&seperator);
    char str[21];
    //itoa(v, str, 10);
    snprintf(str, 10, "%d", v);
    return hash(&str) % Q;
}

int mul(int x, int y) {
    return x * y;
}


